        // for binary search...........................
        public static int BinarySearc(String[] s1, String x)
        {
            int l = 0, r = s1.Length;
            while (l <= r)
            {
                int m = l + (r - l) / 2;

                int res = x.CompareTo(s1[m]);

                // Check if x is present at mid
                if (res == 0)
                    return m;


                // If x greater, ignore left half
                if (res > 0)
                    l = m + 1;

                // If x is smaller, ignore right half
                else
                    r = m - 1;
            }

            return -1;
        }

